import {useState} from "react";
import {Link, useNavigate} from "react-router-dom";
import Warning from "../Warning";
import {useTranslation} from "react-i18next";

const BrandLogin = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const [warning, setWarning] = useState('');

    const [employee] = useState({
        email: "",
        password: ""
    });

    const checkLogin = () => {
        if (employee.email === "" || employee.email.trim().length === 0) {
            setWarning(translate("require-all"))
            return;
        }

        fetch("http://localhost:3001/brand/" + employee.email)
            .then(res => res.json())
            .then(data => {
                if (data === undefined || data.brand.User.password !== employee.password) {
                    setWarning(translate("incorrect_email_or_pass"))
                } else {
                    props.setUser({
                        id: data.brand.id,
                        name: data.brand.User.name,
                        nip: data.brand.nipNumber
                    })
                    navigate("/brand");
                }
            })
            .catch(err => alert(translate("operation_unsuccessful")));
    }

    return (
        <>
            <header>
                <img src="/logo.png" alt="logo" id="logo"/>
                <h1 className="inline">{translate("brand_platform")}</h1>
            </header>
            <div id="login-window" className="container">
                <h1>{translate("log_in_title")}</h1>
                <input type="email" id="email" placeholder={translate("e-mail")} onChange={e => employee.email = e.target.value}/><br/>
                <input type="password" id="password" placeholder={translate("password")} onChange={e => employee.password = e.target.value}/><br/>
                <button onClick={checkLogin}>{translate("login")}</button>
                <Warning message={warning}/><br/>

                <Link to={"/brandregister"}>{translate("register")}</Link>
            </div>
        </>
    );
}

export default BrandLogin;
