import React, {useState, useEffect} from "react";
import {Link, useNavigate, useParams} from "react-router-dom";
import Warning from "../Warning";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";
import {validatePrice} from "../../Utils";

const OfferEdit = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {eventID} = useParams();
    const [offer, setOffer] = useState({
        brandID: props.user.id,
        name: '',
        description: '',
        date: '',
        city: '',
        address: '',
        capacityM: '',
        capacityF: '',
        maxCapacity: '',
        status: '',
        imgURL: '',
    });
    const [warning, setWarning] = useState('');

    useEffect(() => {
        if (eventID !== 'new') {
            fetch("http://localhost:3001/brand/getEvent/" + eventID)
                .then(res => res.json())
                .then(data => setOffer(data[0]))
                .catch(err => alert(translate("operation_unsuccessful")));

            document.getElementById("box-art").src = offer.imgURL;
        }
    }, []);

    if (offer.id === null)
        return <NoPage/>

    const acceptChanges = async (eventID) => {
        if (offer.name === '') {
            setWarning(translate("enter_name"))
            return;
        }

        if (offer.description === '') {
            setWarning(translate("enter_description"))
            return;
        }

        if (offer.date === '') {
            setWarning(translate("enter_date"))
            return;
        }

        if (offer.city === '') {
            setWarning(translate("enter_city"))
            return;
        }

        if (offer.address === '') {
            setWarning(translate("enter_address"))
            return;
        }

        if (offer.maxCapacity === '') {
            setWarning(translate("enter_maxCapacity"))
            return;
        }
        
        if (offer.imgURL.length <= 0) {
            setWarning(translate("enter_image"))
            return;
        }

        if (eventID === 'new') {
            const promise = fetch("http://localhost:3001/putEvent", {
                method: "PUT",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(offer)
            })
                .then(res => res.json())
                .then(data => data.insertId)
                .catch(err => alert(translate("operation_unsuccessful")));


        } else {
            fetch("http://localhost:3001/postEvent/" + eventID, {
                method: "POST",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(offer)
            })
        }

        exit(eventID);
    }

    const exit = (eventID) => {
        if (eventID === 'new')
            navigate("/brand/events");
        else
            navigate("/brand/events");
    }

    return (
        <div className="center container two-columns">
            <h1>{eventID === 'new' ? "Dodaj event" : "Edytuj event"}</h1><br/>
            <div className="form"> 
                <label>{translate("name")}</label><br/>
                <input type="text" defaultValue={offer.name} id="name-input" onChange={e => offer.name = e.target.value}/><br/>
                <label>{translate("description")}</label><br/>
                <input type="text" defaultValue={offer.description} id="description-input" onChange={e => offer.description = e.target.value}/><br/>
                <label>{translate("date")}</label><br/>
                <input type="datetime-local" defaultValue={offer.date ? new Date(offer.date).toISOString().slice(0, 16): ""} id="date-input" onChange={e => offer.date = e.target.value}/><br/>
                <label>{translate("city")}</label><br/>
                <input type="text" defaultValue={offer.city} id="city-input" onChange={e => offer.city = e.target.value}/><br/>
                <label>{translate("address")}</label><br/>
                <input type="text" defaultValue={offer.address} id="address-input" onChange={e => offer.address = e.target.value}/><br/>
                <label>{translate("maxCapacity")}</label><br/>
                <input type="number" defaultValue={offer.maxCapacity} id="maxCapacity-input" onChange={e => offer.maxCapacity = e.target.value}/><br/>
                <label>{translate("image_url")}</label><br/>

                <select
                    id="status-select"
                    defaultValue={event.status || "upcoming"}
                    onChange={handleChange}
                >
                    <option value="upcoming">{translate("upcoming")}</option>
                    {!isNew && (
                      <>
                        <option value="ongoing">{translate("ongoing")}</option>
                        <option value="finished">{translate("finished")}</option>
                      </>
                    )}
                </select>
                <input type="text" defaultValue={offer.imgURL} id="imgURL-input" onChange={e => {
                    offer.imgURL = e.target.value;
                    document.getElementById("box-art").src = e.target.value;
                }}/><br/>

                <img src={offer.image_url} alt="box art" className="large-product-img" id="box-art"/><br/>
                <button onClick={() => acceptChanges(eventID)}>{translate("accept")}</button>
                <button onClick={() => exit(eventID)}>{translate("cancel")}</button>
                <Warning message={warning}/>
            </div>
        </div>
    );
}

export default OfferEdit;
