import {useNavigate, useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";

const ProductDetails = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {offerId} = useParams();
    const [product, setProduct] = useState({});

    useEffect(() => {
        fetch("http://localhost:3001/offers/" + offerId)
            .then(res => res.json())
            .then(data => setProduct(data[0]))
            .catch(err => alert(translate("connection_error")));
    }, []);

    if (product.id === null)
        return <NoPage/>

    const update = async ()=> {
        fetch("http://localhost:3001/update/" + offerId, {
            method: "POST",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                amount: parseInt(product.amount)-1,
                id: props.user.id
            })
        })
            .catch(err => alert(translate("connection-error")));
    }

    const buy = async () => {
        const date = new Date().toISOString().split('.')[0].split('T');

        fetch("http://localhost:3001/purchases/", {
            method: "PUT",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                buyer_id: props.user.id,
                product_id: offerId,
                date: date[0],
                time: date[1],
                purchase_price: product.price 
            })
        })
            .catch(err => alert(translate("operation_unsuccessful")));
        navigate("/thankyou");
    }

    return (
        <div className="center container two-columns">
            <img src={product.image_url} alt="box art" className="large-product-img"/>
            <div id="product-details">
                <h1>{product.name}</h1><br/>
                <strong>{translate("price")}: </strong>{product.price}<br/>
                <strong>{translate("category")}: </strong>{product.category}<br/>
                <strong>{translate("amount")}: </strong>{product.amount}<br/><br/>
                <strong>{translate("description")}: </strong>{product.description}<br/><br/>
                <br></br>
                <h3>{translate("seller_info")}</h3>
                <strong>{translate("seller")}: </strong>{product.seller}<br/>
                
                <br></br>
                <button onClick={() => props.user.id === undefined ? navigate("/login") : update()} disabled={product.amount <= 0}>{translate("buy")}</button><br/>
            
            </div>
        </div>
    );
}

export default ProductDetails;
