import { useNavigate } from "react-router-dom";

function AddTask() {
    const navigate = useNavigate()
    const postTask = () => {
        const userId = document.getElementById("idInput").value;
        const title = document.getElementById("titleInput").value;

        if (!validate(userId, title)) return;

        fetch('https://jsonplaceholder.typicode.com/todos', {
            method: 'POST',
            body: JSON.stringify({
                userId: parseInt(userId),
                title: title,
                completed: false
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
            .then((response) => response.json())
            .then((json) => console.log(json));
        
        navigate("/added")
    }

    function validate(userId, title) {
        const idWarning = document.getElementById("idWarning");
        const titleWarning = document.getElementById("titleWarning");
        if (userId === '' || parseInt(userId) <= 0) {
            idWarning.classList.remove("hidden");
            return false;
        } else {
            idWarning.classList.add("hidden");
        }

        if (title.length < 3) {
            titleWarning.classList.remove("hidden");
            return false;
        } else {
            titleWarning.classList.add("hidden");
        }
        return true;
    }

    return (
        <>
            <h1>Dodaj zadanie</h1>
            <label>id użytkownika</label>
            <input type="number" id="idInput"/>
            <a className="warning hidden" id="idWarning">id musi być dodatnie</a><br/>
            <label>opis</label><br/>
            <textarea id="titleInput"/><br/>
            <a className="warning hidden" id="titleWarning">opis musi mieć co najmniej 3 znaki</a><br/>
            <input type="submit" onClick={postTask} value="dodaj"/>
        </>
    );
}

export default AddTask;
