import {useEffect, useState} from "react";
import {Link, useParams} from "react-router-dom";
import SearchBar from "../SearchBar";
import {useTranslation} from "react-i18next";

const BrandsEvents = () => {
    const [translate, i18n] = useTranslation("global");
    const {brandID} = useParams();
    const [brands, setBrands] = useState([]);
    const [searched, setSearched] = useState('');

    useEffect(() => {
        fetch("http://localhost:3001/brand/getEvents/" + brandID)
            .then(res => res.json())
            .then(data => {
                setBrands(data
                    .filter(c => c.name.includes(searched) || c.id+''===searched)
                )
            });
    }, [searched]);

    const deleteCustomer = (customerId) => {
        if (window.confirm(translate("user_delete_confirm")) === true) {
            fetch("http://localhost:3001/deleteClient/" + customerId, {method: "DELETE"})
        }
    }

    const Brands = brands.map(brand => {
        return (
            <tbody key={brand.id}>
            <tr>
                <th>{brand.id}</th>
                <td>{brand.name}</td>
                <td>{brand.description}</td>
                <td>{new Date(brand.date).toLocaleString()}</td>
                <td>{brand.city}</td>
                <td>{brand.address}</td>
                <td>{brand.maxCapacity}</td>
                <td>{brand.status}</td>
                <td>{<img 
                      src={brand.imgURL} 
                      alt={brand.title} 
                      style={{ width: "80px", height: "80px", objectFit: "cover", borderRadius: "8px" }}
                    />}</td>
                <td><a onClick={() => deleteCustomer(brand.id)} className="underlined">{translate("delete")}</a></td>
            </tr>
            </tbody>
        );
    });

    return (
        <div className="center container">
            <h1>{translate("brands")}</h1>
            <SearchBar setSearched={setSearched}/><br/>
            <table>
                <thead>
                <tr>
                    <th>id</th>
                    <th>{translate("name")}</th>
                    <th>{translate("description")}</th>
                    <th>{translate("date")}</th>
                    <th>{translate("city")}</th>
                    <th>{translate("address")}</th>        
                    <th>{translate("maxCapacity")}</th>
                    <th>{translate("status")}</th>
                    <th>{translate("imgURL")}</th>
                    <th>{translate("delete")}</th>
                </tr>
                </thead>
                {Brands}
            </table>
        </div>
    );
}

export default BrandsEvents;
