import {useNavigate, useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import Warning from "../Warning";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";

const ProductDetails = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {offerId} = useParams();
    const [product, setProduct] = useState({});
    const [sizeID, setSizeID] = useState('');
    const [variants, setVariants] = useState([]);
    const [warning, setWarning] = useState("");

    useEffect(() => {
        fetch("http://localhost:3001/getProductDetails/" + offerId)
            .then(res => res.json())
            .then(data => setProduct(data))
            .catch(err => alert(translate("connection_error")));
        
        fetch("http://localhost:3001/getProductVariants/" + offerId)
            .then(res => res.json())
            .then(data => setVariants(data))
            .catch(err => alert(translate("connection_error")));
    }, []);

    if (product.id === null)
        return <NoPage/>

    //const update = async ()=> {
    //    fetch("http://localhost:3001/update/" + offerId, {
    //        method: "POST",
    //        headers: {'Content-Type': 'application/json'},
    //        body: JSON.stringify({
    //            amount: product.amount,
    //            id: props.user.id
    //        })
    //    })
    //        .catch(err => alert(translate("connection-error")));
    //}
//
    //const buy = async () => {
    //    const date = new Date().toISOString().split('.')[0].split('T');
//
    //    fetch("http://localhost:3001/purchases/", {
    //        method: "PUT",
    //        headers: {'Content-Type': 'application/json'},
    //        body: JSON.stringify({
    //            buyer_id: props.user.id,
    //            product_id: offerId,
    //            date: date[0],
    //            time: date[1],
    //            purchase_price: product.price 
    //        })
    //    })
    //        .catch(err => alert(translate("operation_unsuccessful")));
    //    navigate("/thankyou");
    //}


    const proceedToPayment = () => {
        if(sizeID === "" || sizeID ===undefined){
            setWarning("size_is_not_choosen")
            return
        }else
            navigate("/purchase/"+offerId+"/"+sizeID)

    }


    return (
        <div className="center container two-columns">
            <img src={product.imgURL} alt="box art" className="large-product-img"/>
            <div id="product-details">
                <h1>{product.name}</h1><br/>
                <strong>{translate("price")}: </strong>{product.price}<br/>
                <strong>{translate("category")}: </strong>{product.category}<br/>
                <strong>{translate("description")}: </strong>{product.description}<br/><br/>
                <br></br>
                <h3>{translate("seller_info")}</h3>
                <strong>{translate("seller")}: </strong>{product.Brand?.User?.name}<br/>

                <strong>{translate("sizes")}:</strong><br/>
                <select id="variantSelect" 
                onChange={(e)=> setSizeID(e.target.value)}>
                  <option value="">-- wybierz wariant --</option>
                  {variants.map(v => (
                    <option key={v.id} value={v.id} disabled={v.stock===0}>
                      {v.size} - {v.stock}
                    </option>
                  ))}
                </select>
                <br/>
                <br/>
                <button onClick={() => props.user.id === undefined ? navigate("/login") :navigate("/purchase/"+offerId+"/"+sizeID)}
                 disabled={product.amount <= 0}>{translate("proceed_to_payment")}</button><br/>

                 <Warning message={warning}/>
            
            </div>
            <div id="reviews">
                <strong>{translate("reviews")}: </strong><br/>
            </div>
        </div>
    );
}

export default ProductDetails;
