import {useNavigate, useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import NoPage from "../NoPage";
import Warning from "../Warning";
import {useTranslation} from "react-i18next";

const TournamentDetails = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {tournamentId} = useParams();
    const [tournament, setTournament] = useState({});

    const [warning, setWarning] = useState("");

    useEffect(() => {
        fetch("http://localhost:3001/tournaments/" + tournamentId)
            .then(res => res.json())
            .then(data => setTournament(data[0]))
            .catch(err => alert(translate("connection_error")));
    }, []);

    if (tournament.id === null)
        return <NoPage/>

    const update = async ()=> {
        fetch("http://localhost:3001/updateTournament", {
            method: "POST",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                amount: tournament.amount,
                tournament_id: tournamentId
            })
        })
            .catch(err => alert(translate("connection-error")));
    }

    const signup = async () => {

        const compete = await fetch("http://localhost:3001/compete")
            .then(res => res.json())
            .then(data => data)

        for (const c of compete) {
            if (c.competitor_id === props.user.id && c.tournament_id === tournamentId) {
                setWarning(translate("already signed up"));
                return;
            }
        }

        fetch("http://localhost:3001/compete", {
            method: "PUT",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                competitor_id: props.user.id,
                tournament_id: tournamentId,
            })
        })
            .catch(err => alert(translate("operation_unsuccessful")));
        navigate("/thankyou");
    }

    return (
        <div className="center container two-columns">
            <img src={tournament.image_url} alt="box art" className="large-product-img"/>
            <div id="product-details">
                <h1>{tournament.name}</h1><br/>
                <strong>{translate("organizer")}: </strong>{tournament.organizer}<br/>
                <strong>{translate("place")}: </strong>{tournament.place}<br/>
                <strong>{translate("starting time")}: </strong>{tournament.date} {tournament.time}<br/><br/>
                <strong>{translate("number of competitors")}: </strong>{tournament.amount}/{tournament.max_amount}<br/><br/>
                <br></br>
                <strong>{translate("status")}: </strong>{tournament.status}<br/>
                
                <br></br>
                <button onClick={() => props.user.id === undefined ? navigate("/login") :signup() && update()}
                 disabled={tournament.amount >= tournament.max_amount}>{translate("sign up")}</button><br/>
                 <Warning message={warning}/>
            
            </div>
        </div>
    );
}

export default TournamentDetails;
