import React, {useEffect, useState} from "react";
import {Link, useNavigate} from "react-router-dom";
import {OfferSortOptions, sortOffers} from "../OfferSortOptions";
import {useTranslation} from "react-i18next";
import Pagination from "../Pagination"

const TournamentList = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const [tournaments, setTournaments] = useState([]);
    const [sortOpt, setSortOpt] = useState(0);
    const [priceRangeFrom, setPriceRangeFrom] = useState(0);
    const [priceRangeTo, setPriceRangeTo] = useState(1000);
    const [status, setStatus] = useState("");

    const[currentPage,setCurrentPage] = useState(1);
    const[postPerPage,setPostPerPage] = useState(5);

    useEffect(() => {
        fetch("http://localhost:3001/tournaments")
            .then(res => res.json())
            .then(data => {
                setTournaments(
                    data.filter(prod => prod.name.toLowerCase().includes(props.searched))
                        .filter(prod => prod.status.includes(status))
                        .sort((p1,p2) => sortOffers(p1, p2, sortOpt))
                )
            })
            .catch(err => {
                alert(translate("conection_error"))
                console.log(err);
            });
    }, [props.searched, sortOpt, priceRangeFrom, priceRangeTo]);

    const lastPostIndex = currentPage * postPerPage
    const firstPostIndex = lastPostIndex - postPerPage
    const currentPosts = tournaments.slice(firstPostIndex,lastPostIndex)

    const TournamentList = currentPosts.map((product) => {
        return (
            <div className="container product-card" onClick={() => navigate(product.id + "")} key={product.id}>
                <img src={product.image_url} alt="box art" className="product-img"/>
                <h3>{product.name}</h3>
                {product.price}
                <br/>
                {product.amount > 0 ? "" : translate("sold")}
            </div>
        );
    });

    return (
        <>
        <div id="main">
            <div id="options" className="container">
                <label htmlFor="sort_options"><h3>sortuj</h3></label>
                <OfferSortOptions setSortOpt={setSortOpt}/>
                <h3>filtry</h3>
                <select onChange={e => setStatus(e.target.value)}>
                    <option value="">{translate("-")}</option>
                    <option value="register">{translate("register")}</option>
                    <option value="during">{translate("during")}</option>
                    <option value="finished">{translate("finished")}</option>
                </select>
            </div>
            <div id="product-list">
                {TournamentList}
            </div>
            </div>
        <div><Pagination totalPosts = {tournaments.length} postPerPage = {postPerPage}
                setCurrentPage={setCurrentPage} currentPage = {currentPage}/></div>  
        </>
        
        
        
    );
}

export default TournamentList;
