import { useState } from "react"
import "./App.css"

export function ClimberForm({ onSubmit }) {
  const [newClimber, setNewClimber] = useState("");
  const [blad, setBlad] = useState('');

  function handleSubmit(e) {
    e.preventDefault()
    if (newClimber.length<3){
        setBlad('Imię i nazwisko musi mieć co najmniej 3 znaki.');
        return;
    }
        
    onSubmit(newClimber)
    setNewClimber("")
    setBlad('');
  }

  return (
    <form onSubmit={handleSubmit} className="new-climber-form">
      <div className="form-row">
        <label htmlFor="climber">New Climber</label>
        <input
          value={newClimber}
          onChange={e => setNewClimber(e.target.value)}
          type="text"
          id="climberSurename"
        />
      </div>
      <button className="Add_climber">Add</button>
      <p style={{ color: 'red' }}>{blad}</p>
    </form>
  )
}
