import React, {useState, useEffect} from "react";
import {Link, useNavigate, useParams} from "react-router-dom";
import Warning from "../Warning";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";
import {validatePrice} from "../../Utils";

const ReviewEdit = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {productID} = useParams();
    const [review, setReview] = useState({
        clientID: props.user.id,
        productID: productID,
        rating: '',
        comment: ''
    });
    const [warning, setWarning] = useState('');

    useEffect(() => {
        if (productID !== 'new') {
            fetch("http://localhost:3001/getReview/"+props.user.id + productID)
                .then(res => res.json())
                .then(data => setReview(data))
                .catch(err => alert(translate("operation_unsuccessful")));
        
        
            
            }
    }, []);


    const acceptChanges = async (productID) => {
        if (review.name === '') {
            setWarning(translate("enter_name"))
            return;
        }

        if (review.description === '') {
            setWarning(translate("enter_description"))
            return;
        }

        if (review.date === '') {
            setWarning(translate("enter_date"))
            return;
        }

        if (review.city === '') {
            setWarning(translate("enter_city"))
            return;
        }

        if (review.address === '') {
            setWarning(translate("enter_address"))
            return;
        }

        if (review.maxCapacity === '') {
            setWarning(translate("enter_maxCapacity"))
            return;
        }
        
        if (review.imgURL.length <= 0) {
            setWarning(translate("enter_image"))
            return;
        }

        if (productID === 'new') {
            const promise = fetch("http://localhost:3001/putEvent", {
                method: "PUT",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(review)
            })
                .then(res => res.json())
                .then(data => data.insertId)
                .catch(err => alert(translate("operation_unsuccessful")));


        } else {
            fetch("http://localhost:3001/postEvent/" + productID, {
                method: "POST",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(review)
            })
            
        }

        exit(productID);
    }

    const exit = (productID) => {
        if (productID === 'new')
            navigate("/brand/events");
        else
            navigate("/brand/events");
    }

    return (
        <div className="center container two-columns">
            <h1>{productID === 'new' ? "Dodaj review" : "Edytuj review"}</h1><br/>
            <div className="form"> 
                <label>{translate("name")}</label><br/>
                <input type="text" defaultValue={review.name} id="name-input" onChange={e => review.name = e.target.value}/><br/>
                <label>{translate("description")}</label><br/>
                <input type="text" defaultValue={review.description} id="description-input" onChange={e => review.description = e.target.value}/><br/>
                <label>{translate("date")}</label><br/>
                <input type="datetime-local" defaultValue={review.date ? new Date(review.date).toISOString().slice(0, 16): ""} id="date-input" onChange={e => review.date = e.target.value}/><br/>
                <label>{translate("city")}</label><br/>
                <input type="text" defaultValue={review.city} id="city-input" onChange={e => review.city = e.target.value}/><br/>
                <label>{translate("address")}</label><br/>
                <input type="text" defaultValue={review.address} id="address-input" onChange={e => review.address = e.target.value}/><br/>
                <label>{translate("maxCapacity")}</label><br/>
                <input type="number" defaultValue={review.maxCapacity} id="maxCapacity-input" onChange={e => review.maxCapacity = e.target.value}/><br/>
                <label>{translate("status")}</label><br/>
                <select
                    id="status-select"
                    value ={review.status}
                    onChange={(e) => setReview({ ...review, status: e.target.value })}
                >
                    <option value="upcoming">{translate("upcoming")}</option>
                    {productID!=='new' && (
                      <>
                        <option value="ongoing">{translate("ongoing")}</option>
                        <option value="finished">{translate("finished")}</option>
                      </>
                    )}
                </select><br/>
                <label>{translate("image_url")}</label><br/>
                <input type="text" defaultValue={review.imgURL} id="imgURL-input" onChange={e => {
                    review.imgURL = e.target.value;
                    document.getElementById("box-art").src = e.target.value;
                }}/><br/>

                <img src={review.imgURL} alt="box art" className="large-product-img" id="box-art"/><br/>
                <button onClick={() => acceptChanges(productID)}>{translate("accept")}</button>
                <button onClick={() => exit(productID)}>{translate("cancel")}</button>
                <Warning message={warning}/>
            </div>
        </div>
    );
}

export default ReviewEdit;
