import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import NoPage from "../NoPage";

function TaskDetails(props) {
    const {taskId} = useParams();
    const [taskData, setTaskData] = useState({});
    const [found, setFound] = useState(true);

    useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/todos/' + taskId)
            .then((res) => {
                if (!res.ok) return 
                return res.json();
            })
            .then((json) => {
                setTaskData(json);
            })
            .catch((res) => {
                setFound(false);
            });

    }, []);

    const completeTask = () => {
        fetch('https://jsonplaceholder.typicode.com/todos/' + taskData.id, {
            method: 'PATCH',
            body: JSON.stringify({
                id: taskData.id,
                completed: !taskData.completed
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
            .then((response) => response.json())
            .then((json) => setTaskData(json));
    }

    if (found) {
        return (
            <>
                <h1>Zadanie nr {taskData.id}</h1>
                <a>id użytkownika: {taskData.userId}</a><br/>
                <a>opis: {taskData.title}</a><br/>
                <a>ukończono: {taskData.completed ? 'tak' : 'nie'}</a><br/>
                <button onClick={completeTask}>{taskData.completed ? 'wznów' : 'zakończ'}</button>
            </>
        );
    }
    return <a>Nie znaleziono zadania o podanym id</a>;
}

export default TaskDetails;
