import {useNavigate, useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";

const TournamentDetails = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {tournamentId} = useParams();
    const [tournament, setTournament] = useState({});

    useEffect(() => {
        fetch("http://localhost:3001/tournaments/" + tournamentId)
            .then(res => res.json())
            .then(data => setTournament(data[0]))
            .catch(err => alert(translate("connection_error")));
    }, []);

    if (tournament.id === null)
        return <NoPage/>

    const update = async ()=> {
        fetch("http://localhost:3001/update/" + tournamentId, {
            method: "POST",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                
            })
        })
            .catch(err => alert(translate("connection-error")));
    }

    const buy = async () => {
        const date = new Date().toISOString().split('.')[0].split('T');

        fetch("http://localhost:3001/purchases/", {
            method: "PUT",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                buyer_id: props.user.id,
                product_id: offerId,
                date: date[0],
                time: date[1],
                purchase_price: product.price 
            })
        })
            .catch(err => alert(translate("operation_unsuccessful")));
        navigate("/thankyou");
    }

    return (
        <div className="center container two-columns">
            <img src={tournament.image_url} alt="box art" className="large-product-img"/>
            <div id="product-details">
                <h1>{tournament.name}</h1><br/>
                <strong>{translate("price")}: </strong>{tournament.price}<br/>
                <strong>{translate("category")}: </strong>{tournament.category}<br/>
                <strong>{translate("amount")}: </strong>{tournament.amount}<br/><br/>
                <strong>{translate("description")}: </strong>{tournament.description}<br/><br/>
                <br></br>
                <h3>{translate("seller_info")}</h3>
                <strong>{translate("seller")}: </strong>{tournament.seller}<br/>
                
                <br></br>
                <button onClick={() => props.user.id === undefined ? navigate("/login") :buy() && update()}
                 disabled={tournament.amount <= 0}>{translate("buy")}</button><br/>
            
            </div>
        </div>
    );
}

export default TournamentDetails;
