import {useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import {Todo} from "./Todo";
import NoPage from "../NoPage";

function TaskDetails(props) {
    const {taskId} = useParams();
    const [taskData, setTaskData] = useState({});
    const [found, setFound] = useState(true);

    useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/todos/' + taskId)
            .then((res) => {
                if (!res.ok) return Promise.reject(console.error("nie istnieje")
                );
                return res.json();
            })
            .then((json) => {
                return <Todo {...json} completeTask={completeTask}></Todo>
            })
            .catch((res) => {
                setFound(false);
            });

    }, []);

    const completeTask = () => {
        fetch('https://jsonplaceholder.typicode.com/todos/' + taskData.id, {
            method: 'PATCH',
            body: JSON.stringify({
                id: taskData.id,
                completed: !taskData.completed
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
            .then((response) => response.json())
            .then((json) => setTaskData(json));
    }

    
    return <a>Nie znaleziono zadania o podanym id</a>;
}

export default TaskDetails;
