import { useEffect, useState } from "react"
import { ClimberForm } from "./ClimberForm"
import { ClimberList } from "./ClimberList"

export default function App() {
  const [climbers, setClimbers] = useState(() => {
    const localValue = localStorage.getItem("ITEMS")
    if (localValue == null) return []

    return JSON.parse(localValue)
  })

  useEffect(() => {
    localStorage.setItem("ITEMS", JSON.stringify(climbers))
  }, [climbers])

  function addClimber(title) {
    setClimbers(currentClimbers => {
      return [
        ...currentClimbers,
        { id: crypto.randomUUID(), title, completed: false },
      ]
    })
  }
  function deleteClimber(id) {
    setClimbers(currentClimbers => {
      return currentClimbers.filter(Climber => Climber.id !== id)
    })
  }

  return (
    <>
      <ClimberForm onSubmit={addClimber} />
      <h1 className="header">Climbers List</h1>
      <ClimberList climbers={climbers} deleteClimber={deleteClimber} />
    </>
  )
}
