import { useEffect, useState } from "react"
import "./App.css"
import { Link, Route, Routes } from "react-router-dom"
import { Home } from "./pages/Home"
import { NewClimber } from "./pages/NewClimber"
import { ClimberList } from "./pages/ClimberList"

export default function App() {
  const [climbers, setClimbers] = useState(() => {
    const localvalue = localStorage.getItem("CLIMBERS")
    if(localvalue === null) return []

    return JSON.parse(localvalue)
  })

  useEffect (() => {
    localStorage.setItem("CLIMBERS", JSON.stringify(climbers))
  },[climbers])

  function addClimber(title) {
    setClimbers(currentClimbers => {
      return [
        ...currentClimbers,
        { id: crypto.randomUUID(), title},
      ]
    })
  }
  function deleteClimber(id) {
    setClimbers(currentClimbers => {
      return currentClimbers.filter(Climber => Climber.id !== id)
    })
  }

  return (
    <>
      <body>
        <nav>
          <ul>
            <li>
              <Link to = "/">Home</Link>
            </li>
            <li>
              <Link to = "/NewClimber">New Climber</Link>
            </li>
            <li>
              <Link to = "/ClimberList">Climber List</Link>
            </li>
          </ul>
        </nav>
      </body>
      <Routes>
        <Route to = "/" element = {< Home/>}></Route>
        <Route to = "/NewClimber" element = {< NewClimber/>}></Route>
        <Route to = "/ClimberList" element = {< ClimberList/>}></Route>
      </Routes>
    </>
  )
}
