import React, {useState, useEffect} from "react";
import {Link, useNavigate, useParams} from "react-router-dom";
import Warning from "../Warning";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";
import {validatePrice} from "../../Utils";

const Purchase = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {offerID, variantID} = useParams();
    const [product, setProduct] = useState([]);
    const [variant, setVariant] = useState([]);
    const [order, setOrder] = useState({
        clientID: props.user.id,
        productID: offerID,
        name: '',
        surname: '',
        size: variant?.size,
        address: '',
        status: ''
    });
    const [warning, setWarning] = useState('');

    useEffect(() => {
        fetch("http://localhost:3001/getProductDetails/" + offerID)
            .then(res => res.json())
            .then(data => setProduct(data))
            .catch(err => alert(translate("connection_error1")));
        
        fetch("http://localhost:3001/getVariant/" + variantID)
            .then(res => res.json())
            .then(data => setVariant(data))
            .catch(err => alert(translate("connection_error")));
    }, []);

    if (order.id === null)
        return <NoPage/>

    const acceptChanges = async (offerID) => {
        if (order.name === '') {
            setWarning(translate("enter_name"))
            return;
        }
        if (!validatePrice(order.price)) {
            setWarning(translate("incorrect_price"))
            return;}
        
        if (order.category.length <= 0) {
            setWarning(translate("choose category"))
            return;
        }
        if (order.imageURL.length <= 0) {
            setWarning(translate("enter_image"))
            return;
        }

        if (offerID === 'new') {
            const promise = fetch("http://localhost:3001/putProduct", {
                method: "PUT",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(order)
            })
                .then(res => res.json())
                .then(data => data.insertId)
                .catch(err => alert(translate("operation_unsuccessful")));


        } else {
            fetch("http://localhost:3001/postProduct/" + offerID, {
                method: "POST",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(order)
            })
        }

        //exit(offerID);
    }

    const exit = (offerID) => {
        if (offerID === 'new')
            navigate("/brand/products");
        else
            navigate("/brand/products");
    }

    return (
        <div className="center container two-columns">
            <h1>{translate("summary")}</h1><br/>
            <strong>{translate("product")}: {product.name}</strong> <br/>
            <strong>{translate("size")}: {variant.size}</strong> <br/>
            <div className="form"> 
                <label>{translate("name")}</label><br/>
                <input type="text" defaultValue={order.name} id="name-input" onChange={e => order.name = e.target.value}/><br/>
                <label>{translate("price")} [USD]</label><br/>
                <input type="number" defaultValue={order.price} id="price-input" onChange={e => order.price = e.target.value}/><br/>
                <label>{translate("description")}</label><br/>
                <input type="text" defaultValue={order.description} id="description-input" onChange={e => order.description = e.target.value}/><br/>
                <label>{translate("category")}</label><br/>
                <select onChange={e => order.category = e.target.value}>
                    <option value=''>{translate("choose category")}</option>
                    <option value='obuwie'>{translate("obuwie")}</option>
                    <option value='akcesoria'>{translate("akcesoria")}</option>
                    <option value='przyrzad'>{translate("przyrzad")}</option>
                    <option value='odziez'>{translate("odziez")}</option>
                </select>
                <label>{translate("image_url")}</label><br/>
                <input type="text" defaultValue={order.imageURL} id="url-input" onChange={e => {
                    order.imageURL = e.target.value;
                    document.getElementById("box-art").src = e.target.value;
                }}/><br/>

                <img src={order.image_url} alt="box art" className="large-product-img" id="box-art"/><br/>
                <button onClick={() => acceptChanges(offerID)}>{translate("accept")}</button>
                <button onClick={() => exit(offerID)}>{translate("cancel")}</button>
                <Warning message={warning}/>
            </div>
        </div>
    );
}

export default Purchase;
