import {useNavigate, useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";

const TournamentDetails = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {eventID} = useParams();
    const [event, setEvent] = useState({});

    useEffect(() => {
        fetch("http://localhost:3001/getEvent/" + eventID)
            .then(res => res.json())
            .then(data => setEvent(data[0]))
            .catch(err => alert(translate("connection_error")));
    }, []);

    if (event.id === null)
        return <NoPage/>

    const signup = async () => {


        fetch("http://localhost:3001/compete", {
            method: "PUT",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                competitor_id: props.user.id,
                tournament_id: eventID,
            })
        })
            .catch(err => alert(translate("operation_unsuccessful")));
        navigate("/thankyou");
    }

    return (
        <div className="center container two-columns">
            <img src={event.image_url} alt="box art" className="large-product-img"/>
            <div id="product-details">
                <h1>{event.name}</h1><br/>
                <strong>{translate("organizer")}: </strong>{event.name}<br/>
                <strong>{translate("place")}: </strong>{event.place}<br/>
                <strong>{translate("starting time")}: </strong>{event.date} {event.time}<br/><br/>
                <strong>{translate("number of competitors")}: </strong>{event.amount}/{event.max_amount}<br/><br/>
                <br></br>
                <strong>{translate("status")}: </strong>{event.status}<br/>
                
                <br></br>
                <button onClick={() => props.user.id === undefined ? navigate("/login") :signup() }
                 disabled={event.amount >= event.max_amount}>{translate("sign up")}</button><br/>
            
            </div>
        </div>
    );
}

export default TournamentDetails;
