import {useNavigate, useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";

const TournamentDetails = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const [signed, setSigned] = useState();
    const {eventID} = useParams();
    const [event, setEvent] = useState({});

    useEffect(() => {
        fetch("http://localhost:3001/getEvent/" + eventID)
            .then(res => res.json())
            .then(data => setEvent(data[0]))
            .catch(err => alert(translate("connection_error")));
    
        fetch("http://localhost:3001/getParticipation/"+props.id+"/" + eventID)
            .then(res => res.json())
            .then(data => setSigned(data[0]))
            .catch(err => alert(translate("tu zle")));

        }, []);

    if (event.id === null)
        return <NoPage/>

    const signup = async () => {


        fetch("http://localhost:3001/putParticipation", {
            method: "PUT",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                clientID: props.id,
                eventID: eventID,
            })
        })
            .catch(err => alert(translate("operation_unsuccessful")));
        navigate("/thankyou");
    }

    return (
        <div className="center container two-columns">
            <img src={event.imgURL} alt="box art" className="large-product-img"/>
            <div id="product-details">
                <h1>{event.name}</h1><br/>
                <strong>{translate("organizer")}: </strong>{event.Brand?.User?.name || "Brak danych"}<br/>
                <strong>{translate("place")}: </strong>{event.city}, {event.address}<br/>
                <strong>{translate("starting time")}: </strong>{new Date(event.date).toLocaleString()}<br/><br/>
                <strong>{translate("number of competitors")}: </strong>{event.amount}/{event.max_amount}<br/><br/>
                <strong>{translate("status")}: </strong>{event.status}<br/>
                <strong>{translate("description")}: </strong>{event.description}<br/>
                
                <br></br>
                <button onClick={() => props.user.id === undefined ? navigate("/login") :signup()} disabled = {signed!==undefined}>
                    {translate("sign up")}</button><br/>
            
            </div>
        </div>
    );
}

export default TournamentDetails;
