import React, { useState } from 'react';
import Wspinacz from './Wspinacz';
import Walidator from './Walidator';

const ListaWspinaczy = () => {
  const [wspinacze, setWspinacze] = useState([]);
  const [nowyWspinacz, setNowyWspinacz] = useState('');
  const [blad, setBlad] = useState('');

  const dodajWspinacza = () => {
    if (nowyWspinacz.length < 3) {
      setBlad('Imię i nazwisko musi mieć co najmniej 3 znaki.');
      return;
    }
    setWspinacze([...wspinacze, nowyWspinacz]);
    setNowyWspinacz('');
    setBlad('');
  };

  const usunWspinacza = (index) => {
    const nowaLista = wspinacze.filter((_, i) => i !== index);
    setWspinacze(nowaLista);
  };

  return (
    <div>
      <h1>Lista Wspinaczy</h1>
      <input
        type="text"
        value={nowyWspinacz}
        onChange={(e) => setNowyWspinacz(e.target.value)}
      />
      <button onClick={dodajWspinacza}>Dodaj Wspinacza</button>
      {blad && <p style={{ color: 'red' }}>{blad}</p>}
      <ul>
        {wspinacze.map((wspinacz, index) => (
          <Wspinacz
            key={index}
            index={index}
            wspinacz={wspinacz}
            usunWspinacza={usunWspinacza}
          />
        ))}
      </ul>
    </div>
  );
};

export default ListaWspinaczy;
