import { useState } from "react"

export function ClimberForm({ onSubmit }) {
  const [name, setName] = useState("");
  const [surename, setSurename] = useState("");
  const [blad, setBlad] = useState('');
  
  function handleSubmit(e) {
    e.preventDefault()
    if (name.length<3 || surename.length<3){
        setBlad('Imię i nazwisko musi mieć co najmniej 3 znaki.');
        return;
    }
        
    onSubmit(name+" "+surename)
    setNewClimber("")
    setBlad('');
  }

  return (
    
    <form onSubmit={handleSubmit} className="new-climber-form">
      <div className="form-row">
        <label>Imię</label>
        <input
          value={name}
          onChange={e => setName(e.target.value)}
          type="text"
          id="climbername"
        />
        <label>Nazwisko</label>
        <input
          value={surename}
          onChange={e => setSurename(e.target.value)}
          type="text"
          id="climberSurename"
        />
      </div>
      <button className="Add_climber">Add</button>
      <p style={{ color: 'red' }}>{blad}</p>
    </form>
    
  )
}
