import { useEffect, useState } from "react"
import "./App.css"
import { Link, Route, Routes } from "react-router-dom"
import { Home } from "./pages/Home"
import { NewClimber } from "./pages/NewClimber"
import { ClimberList } from "./pages/ClimberList"
import { SearchClimber } from "./pages/SearchClimber"
import { NotFound } from "./pages/NotFound"
import { FoundClimber } from "./pages/FoundClimber"
import { AddedClimber } from "./pages/AddedClimber"
import data from "./data/Climbers.JSON"


export default function App() {
  const [climbers, setClimbers] = useState([])

  useEffect(() => {
    fetch("/climbers.json")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        return response.json();
      })
      .then((data) => setClimbers(data))
      .catch((error) => console.error("Error loading climbers:", error));
  }, []);

  function addClimber(name,surename,age) {
    setClimbers(currentClimbers => {
      return [
        ...currentClimbers,
        { id: climbers?.length ? climbers[climbers.length - 1].id + 1 : 1
          , name:name, surename:surename, age:age},
      ]
    })
  }

  return (
    <>
      <body>
        <nav>
          <ul>
            <li>
              <Link to = "/NewClimber">New Climber</Link>
            </li>
            <li>
              <Link to = "/ClimberList">Climber List</Link>
            </li>
            <li>
              <Link to = "/SearchClimber">Search Climber</Link>
            </li>
          </ul>
        </nav>
        <Routes>
        <Route path = "/" element = {<Home/>}></Route>
        <Route path = "/NewClimber" element = {< NewClimber onSubmit = {addClimber}/>}></Route>
        <Route path = "/ClimberList" element = {< ClimberList climbers={climbers}/>}></Route>
        <Route path = "/AddedClimber" element = {<AddedClimber/>}></Route>
        <Route path="/SearchClimber" element = {< SearchClimber/>}></Route>
        <Route path = "/SearchClimber/:climberID" element = {<FoundClimber climbers={climbers}/>}></Route>
        <Route path = "*" element = {<NotFound/>}></Route>
      </Routes>
      </body>

    </>
  )
}
