import React, {useState, useEffect} from "react";
import {Link, useNavigate, useParams} from "react-router-dom";
import Warning from "../Warning";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";
import {validatePrice} from "../../Utils";

const OrderEdit = (props) => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {orderID} = useParams();
    const [offer, setOffer] = useState({});
    const [warning, setWarning] = useState('');

    useEffect(() => {
        fetch("http://localhost:3001/getOrder/" + orderID)
            .then(res => res.json())
            .then(data => setOffer(data))
            .catch(err => alert(translate("operation_unsuccessful")));
    
    }, []);

    if (offer.id === null)
        return <NoPage/>

    const acceptChanges = async (orderID) => {
        if (offer.name === '') {
            setWarning(translate("enter_name"))
            return;
        }
        if (!validatePrice(offer.price)) {
            setWarning(translate("incorrect_price"))
            return;}
        
        if (offer.category.length <= 0) {
            setWarning(translate("choose category"))
            return;
        }
        if (offer.imgURL.length <= 0) {
            setWarning(translate("enter_image"))
            return;
        }

        fetch("http://localhost:3001/postProduct/" + orderID, {
            method: "POST",
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(offer)
        })
        
        exit(orderID);
    }

    const exit = (orderID) => {
        if (orderID === 'new')
            navigate("/brand/products");
        else
            navigate("/brand/products");
    }

    return (
        <div className="center container two-columns">
            <h1>{translate('edit_order')}</h1><br/>
            <div className="form"> 
                <label>{translate("name")}</label><br/>
                <input type="text" defaultValue={offer.name} id="name-input" onChange={e => offer.name = e.target.value}/><br/>
                <label>{translate("surname")}</label><br/>
                <input type="text" defaultValue={offer.surname} id="name-input" onChange={e => offer.surname = e.target.value}/><br/>
                <label>{translate("size")}</label><br/>
                <strong>{offer.size}</strong>
                <label>{translate("price")}</label><br/>
                <input type="text" defaultValue={offer.price} id="description-input" onChange={e => offer.price = e.target.value}/><br/>
                <label>{translate("address")}</label><br/>
                <input type="text" defaultValue={offer.address} id="description-input" onChange={e => offer.address = e.target.value}/><br/>
                <label>{translate("category")}</label><br/>
                <select value={offer.status} onChange={e => setOffer({...offer, status: e.target.value})}>
                    <option value='pending'>{translate("pending")}</option>
                    <option value='processing'>{translate("processing")}</option>
                    <option value='completed'>{translate("completed")}</option>
                </select>
                <br/>
                <button onClick={() => acceptChanges(orderID)}>{translate("accept")}</button>
                <button onClick={() => exit(orderID)}>{translate("cancel")}</button>
                <Warning message={warning}/>
            </div>
        </div>
    );
}

export default OrderEdit;
