import React, {useState, useEffect} from "react";
import {useParams, Link} from "react-router-dom";
import SearchBar from "../SearchBar";
import {useTranslation} from "react-i18next";

const Reviews = (props) => {
    const [offers, setOffers] = useState([]);
    const {productID} = useParams();
    const [translate, i18n] = useTranslation("global");

    useEffect(() => {
        fetch("http://localhost:3001/getProductReviews/"+ productID)
            .then(res => res.json())
            .then(data => setOffers(data))
            .catch(err => alert(translate("operation_unsuccessful")));
    }, []);


    const offerList = offers.map((offer) => {
        return (
            <tbody key={offer.id}>
            <tr>
                <th>{offer.id}</th>
                <td>{offer.Client?.User?.name}</td>
                <td>{offer.description}</td>
                <td>{offer.price}</td>
                <td>{offer.category}</td>
                <td><Link to={"/brand/productVariant/"+offer.id}>{translate("add_amount")}</Link></td>
                <td><Link to={offer.id + "/"}>{translate("details")}</Link></td>
                <td><Link to={offer.id + "/"}>{translate("edit")}</Link></td>
            </tr>
            </tbody>
        );
    });

    return (
        <div className="center container">
            <h1>{translate("offers")}</h1>
            <Link to="new">{translate("add_offer")}</Link><br/>
            <table>
                <thead>
                <tr>
                    <th>id</th>
                    <th>{translate("name")}</th>
                    <th>{translate("rating")}</th>
                    <th>{translate("comment")}</th>
                </tr>
                </thead>
                {offerList}
            </table>
        </div>
    );
};

export default Reviews;
