import React, {useState, useEffect} from "react";
import {Link, useNavigate, useParams} from "react-router-dom";
import Warning from "../Warning";
import NoPage from "../NoPage";
import {useTranslation} from "react-i18next";
import {validatePrice} from "../../Utils";

const OfferEdit = () => {
    const navigate = useNavigate();
    const [translate, i18n] = useTranslation("global");
    const {offerId} = useParams();
    const [offer, setOffer] = useState({
        id: '',
        name: '',
        price: '',
        seller: '',
        description: '',
        amount: 0,
        category: '',
        image_url: ''
    });
    const [warning, setWarning] = useState('');

    useEffect(() => {
        if (offerId !== 'new') {
            fetch("http://localhost:3001/offers/" + offerId)
                .then(res => res.json())
                .then(data => setOffer(data[0]))
                .catch(err => alert(translate("operation_unsuccessful")));
        }
    }, []);

    if (offer.id === null)
        return <NoPage/>

    const acceptChanges = async (offerId) => {
        if (offer.name === '') {
            setWarning(translate("enter_name"))
            return;
        }
        if (!validatePrice(offer.price)) {
            setWarning(translate("incorrect_price"))
            return;}
        if (offer.seller === undefined || offer.seller.length <= 0) {
            setWarning(translate("enter_seller"))
            return;
        }
        if (isNaN(parseInt(offer.amount)) || offer.amount < 0) {
            setWarning(translate("incorrect_amount"))
            return;
        }
        if (offer.category.length <= 0) {
            setWarning(translate("choose category"))
            return;
        }
        if (offer.image_url.length <= 0) {
            setWarning(translate("enter_image"))
            return;
        }

        if (offerId === 'new') {
            const promise = fetch("http://localhost:3001/offers", {
                method: "PUT",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(offer)
            })
                .then(res => res.json())
                .then(data => data.insertId)
                .catch(err => alert(translate("operation_unsuccessful")));


        } else {
            fetch("http://localhost:3001/offers/" + offerId, {
                method: "POST",
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(offer)
            })
        }

        exit(offerId);
    }

    const exit = (offerId) => {
        if (offerId === 'new')
            navigate("/emp/offers");
        else
            navigate("/emp/offers/" + offerId);
    }

    return (
        <div className="center container two-columns">
            <h1>{offerId === 'new' ? "Dodaj ofertę" : "Edytuj ofertę"}</h1><br/>
            <div className="form"> 
                <label>{translate("name")}</label><br/>
                <input type="text" defaultValue={offer.name} id="name-input" onChange={e => offer.name = e.target.value}/><br/>
                <label>{translate("price")} [USD]</label><br/>
                <input type="number" defaultValue={offer.price} id="price-input" onChange={e => offer.price = e.target.value}/><br/>
                <label>{translate("seller")}</label><br/>
                <input type="text" defaultValue={offer.seller} id="seller-input" onChange={e => offer.seller = e.target.value}/><br/>
                <label>{translate("description")}</label><br/>
                <input type="text" defaultValue={offer.description} id="description-input" onChange={e => offer.description = e.target.value}/><br/>
                <label>{translate("amount")}</label><br/>
                <input type="text" defaultValue={offer.amount} id="amount-input" onChange={e => offer.amount = e.target.value}/><br/>
                <label>{translate("category")}</label><br/>
                <select onChange={e => offer.category = e.target.value}>
                    <option value=''>{translate("choose category")}</option>
                    <option value='obuwie'>{translate("obuwie")}</option>
                    <option value='akcesoria'>{translate("akcesoria")}</option>
                    <option value='przyrzad'>{translate("przyrzad")}</option>
                    <option value='odziez'>{translate("odziez")}</option>
                </select>
                <label>{translate("image_url")}</label><br/>
                <input type="text" defaultValue={offer.image_url} id="url-input" onChange={e => {
                    offer.image_url = e.target.value;
                    document.getElementById("box-art").src = e.target.value;
                }}/><br/>

                <img src={offer.image_url} alt="box art" className="large-product-img" id="box-art"/><br/>
                <button onClick={() => acceptChanges(offerId)}>{translate("accept")}</button>
                <button onClick={() => exit(offerId)}>{translate("cancel")}</button>
                <Warning message={warning}/>
            </div>
        </div>
    );
}

export default OfferEdit;
