import './App.css';
import {BrowserRouter, Route, Routes} from "react-router-dom";
import {useState} from "react";

import NoPage from "./Components/NoPage"

import Layout from "./Components/Customer/Layout";
import ProductList from "./Components/Customer/ProductList";
import ProductDetails from "./Components/Customer/ProductDetails";
import ThankYou from "./Components/Customer/ThankYou";
import Login from "./Components/Customer/Login";
import Register from "./Components/Customer/Register";
import Profile from "./Components/Customer/Profile";
import PurchaseHistory from "./Components/Customer/PurchaseHistory";
import CompetitionHistory from "./Components/Customer/CompetitionHistory"
import ProfileEdit from "./Components/Customer/ProfileEdit";
import TournamentList from './Components/Customer/TournamentList';
import TournamentDetails from './Components/Customer/TournamentDetails';


import EmpLayout from "./Components/Employee/EmpLayout";
import PurchaseDetails from "./Components/Employee/PurchaseDetails";
import CustomerDetails from "./Components/Employee/CustomerDetails";
import CustomerEdit from "./Components/Employee/CustomerEdit";
import EmpPurchaseHistory from "./Components/Employee/EmpPurchaseHistory";
import PurchaseEdit from "./Components/Employee/PurchaseEdit";
import CustomerList from "./Components/Employee/CustomerList";
import Home from "./Components/Employee/Home";
import EmpLogin from "./Components/Employee/EmpLogin";
import OfferDetails from "./Components/Employee/OfferDetails";
import OfferEdit from "./Components/Employee/OfferEdit";
import OfferList from "./Components/Employee/OfferList";
import PurchaseList from "./Components/Employee/PurchaseList";

import BrandRegister from "./Components/Brand/BrandRegister"
import BrandLogin from "./Components/Brand/BrandLogin";
import BrandLayout from "./Components/Brand/BrandLayout"
import BrandProductList from "./Components/Brand/ProductList"
import BrandProductEdit from "./Components/Brand/ProductEdit"
import BrandProductVariant from "./Components/Brand/ProductVariant"



function App() {
    const [user, setUser] = useState({});
    const [language, setLanguage] = useState('pl');
    const [searched, setSearched] = useState('')

    return (
        <BrowserRouter>
            <Routes>
                <Route path="/" element={<Layout setLanguage={setLanguage} user={user} setSearched={setSearched}/>}>
                    <Route path="login" element={<Login setUser={setUser}/>}/>
                    <Route path="register" element={<Register/>}/>
                    <Route index element={<ProductList searched={searched} setSearched={setSearched}/>}/>
                    <Route path=":offerId" element={<ProductDetails user={user}/>}/>
                    <Route path='tournament' element={<TournamentList searched={searched}/>}/>
                    <Route path='tournament/:tournamentId' element={<TournamentDetails user={user}/>}/>
                    <Route path='article' element={<h1>dwa</h1>}/>
                    <Route path='article/:articleID' element={<h1>dwa</h1>}/>
                    <Route path="thankyou" element={<ThankYou/>}/>
                    <Route path="profile" element={<Profile user={user}/>}>
                        <Route path="purchases" element={<PurchaseHistory user={user}/>}/>
                        <Route path="competition" element={<CompetitionHistory user={user}/>}/>
                    </Route>
                    <Route path="profile/edit" element={<ProfileEdit user={user}/>}/>
                    
                </Route>

                <Route path="/brandregister" element={<BrandRegister/>}/>
                <Route path="/brandlogin" element={<BrandLogin setUser={setUser}/>}/>
                <Route path='/brand' element={<BrandLayout setLanguage={setLanguage} user={user}/>}>
                    <Route index element={<Home user={user}/>}/>
                    <Route path="products" element={<BrandProductList user={user}/>}/>
                    <Route path="products/:productID" element={<BrandProductEdit user={user}/>}/>
                    <Route path='productVariant/:productID' element={<BrandProductVariant/>}/>
                </Route>

                <Route path="/emplogin" element={<EmpLogin setUser={setUser}/>}/>
                <Route path="/emp" element={<EmpLayout setLanguage={setLanguage} user={user}/>}>
                    <Route index element={<Home user={user}/>}/>

                    <Route path="offers" element={<OfferList/>}/>
                    <Route path="offers/:offerId" element={<OfferDetails/>}/>
                    <Route path="edit_offer/:offerId" element={<OfferEdit/>}/>

                    <Route path="purchases" element={<PurchaseList/>}/>
                    <Route path="purchases/:purchaseId" element={<PurchaseDetails/>}/>
                    <Route path="edit_purchase/:purchaseId" element={<PurchaseEdit/>}/>

                    <Route path="customers" element={<CustomerList/>}/>
                    <Route path="customers/:customerId" element={<CustomerDetails/>}>
                        <Route path="purchases" element={<EmpPurchaseHistory/>}/>
                        <Route path="competition" element={<EmpPurchaseHistory/>}/>
                    </Route>
                    <Route path="edit_customer/:customerId" element={<CustomerEdit/>}/>
                </Route>

                <Route path="*" element={<NoPage/>}/>
            </Routes>
        </BrowserRouter>
    );
}

export default App;
