import { useState } from "react"
import { redirect } from "react-router-dom";
export function NewClimber({onSubmit}){
    
  const [name, setName] = useState("");
  const [surename, setSurename] = useState("");
  const [age, setAge] = useState("");
  const [blad, setBlad] = useState('');

  function handleSubmit(e) {
    e.preventDefault()
    if (name.length<3 || surename.length<3){
        setBlad('Imię i nazwisko musi mieć co najmniej 3 znaki.');
        return;
    }
        
    onSubmit(name,surename,age)
    setName("")
    setSurename("");
    setAge("")
    setBlad('');
    redirect("/")
  }

  return (
    
    <form onSubmit={handleSubmit} className="new-climber-form">
      <div className="form-row">
        <label>Imię</label>
        <input
          value={name}
          onChange={e => setName(e.target.value)}
          type="text"
          id="climbername"
        required />
        <br/>
        <label>Nazwisko</label>
        <input
          value={surename}
          onChange={e => setSurename(e.target.value)}
          type="text"
          id="climberSurename"
        required />
        <br/>
        <label>Nazwisko</label>
        <input
          value={age}
          onChange={e => setAge(e.target.value)}
          type="number"
          id="climberAge"
        required />
      </div>
      <button className="Add_climber">Add</button>
      <p style={{ color: 'red' }}>{blad}</p>
    </form>
    
  )
}
